import { GET_ITEMS, ADD_ITEM, DELETE_ITEM, ITEMS_LOADING } from '../actions/types';

const initialState = {
    items: [],
    loading: false
};

export default function ( state = initialState, action ) {
    switch(action.type){
        case GET_ITEMS:
            return {
                ...state
            };
        case DELETE_ITEM:
            return {
                ...state,
                items: state.items.filter(item => item.id !== action.payload)
            };
        case ADD_ITEM:
            return {
                ...state,
                items: [action.payload, ...state.items] // adds a row via the state manager
            };
        case ITEMS_LOADING:
            return {
                ...state,
                loading: true
            }
        default:
            return state
    };
};
